INSERT INTO articles (NAME, Brand, DESCRIPTION, Price, Stock, Imagepath, Image) VALUES ("Air Force 1 '07", "Nike", "Le charme continue d'opérer avec la Nike Air Force 1 ’07. Cette chaussure de basketball OG revisite ses éléments les plus célèbres : les renforts cousus résistants, les finitions sobres et juste ce qu'il faut d'éclat pour vous faire briller.", 120, 0, "/images/articles/Air Force 1 '07.png", "Air Force 1 '07.png");
INSERT INTO articles (NAME, Brand, DESCRIPTION, Price, Stock, Imagepath, Image) VALUES ("Yeezy FoamRunners", "Adidas", "En termes de design, la Yeezy Foam Runner est dépourvue de lacets et de pièces mobiles, et arbore une construction extraterrestre/poreuse dont l'arrière massif rappelle la adidas Yeezy 700.", 200, 0, "/images/articles/Yeezy FoamRunners.png", "Yeezy FoamRunners.png");
INSERT INTO articles (NAME, Brand, DESCRIPTION, Price, Stock, Imagepath, Image) VALUES ("Yeezy boost 350 v2 Zebra", "Adidas", "La combinaison signature de la 350 V2 en une seule pièce Primeknit et de la semelle intermédiaire Boost offre la qualité et le confort qui font la réputation de la série Yeezy Boost. Les lacets épais, la languette arrière en toile et le marquage rouge 'SPLY-350' sur la paroi latérale sont autant de caractéristiques propres à l'outillage de la 350 V2.", 1044, 0, "/images/articles/Yeezy boost 350 v2 Zebra.png", "Yeezy boost 350 v2 Zebra.png");
INSERT INTO articles (NAME, Brand, DESCRIPTION, Price, Stock, Imagepath, Image) VALUES ("Jordan 1 Retro Low OG SP", "Jordan", "Sortie exclusivement sur l'application SNKRS et le site web de Scott, la Jordan 1 Retro Low OG SP présente le swoosh inversé signature du rappeur sur le côté latéral de la chaussure. Le nubuck noir et le daim moka foncé occupent une place prépondérante sur l'empeigne, tandis que le Cactus Jack et le logo Nike se détachent en University Red.", 1044, 0, "/images/articles/Jordan 1 Retro Low OG SP.png", "Jordan 1 Retro Low OG SP.png");
INSERT INTO articles (NAME, Brand, DESCRIPTION, Price, Stock, Imagepath, Image) VALUES ("Air Jordan REtro High TravisScott CactusJack", "Travis Scott", "La Travis Scott x Air Jordan 1 High OG TS SP offre un nouveau look à la silhouette iconique, grâce à un Swoosh surdimensionné orienté vers l'arrière sur le côté latéral. Un Swoosh traditionnel orne le côté médial de la tige, qui est composée d'un mélange de cuir blanc et de daim marron.", 1284, 0, "/images/articles/Air Jordan REtro High TravisScott CactusJack.png", "Air Jordan REtro High TravisScott CactusJack.png");
INSERT INTO articles (NAME, Brand, DESCRIPTION, Price, Stock, Imagepath, Image) VALUES ("Air Jordan purple High", "Jordan", "Lancée en 2020, la Air Jordan 1 Retro High OG 'Court Purple 2.0' est une sorte de suite au colorway 2018 qui présentait les mêmes accents violets. Contrairement à son prédécesseur, cette paire utilise un color blocking rappelant le colorway OG 'Chicago', Court Purple remplaçant le panneau rouge de l'original.", 195, 0, "/images/articles/Air Jordan purple High.png", "Air Jordan purple High.png");
INSERT INTO articles (NAME, Brand, DESCRIPTION, Price, Stock, Imagepath, Image) VALUES ("Air Jordan Dior", "Dior", "Les baskets Dior x Air Jordan 1 High sont la première collaboration entre la maison de luxe et le géant des baskets. Confectionnées en cuir gris loup et blanc voile, ces baskets Air Jordan 1 High reprennent la silhouette emblématique de la chaussure montante et l'actualisent en y ajoutant un logo swoosh monogramme Dior gris.", 6607, 0, "/images/articles/Air Jordan Dior.png", "Air Jordan Dior.png");
INSERT INTO articles (NAME, Brand, DESCRIPTION, Price, Stock, Imagepath, Image) VALUES ("Air jordan 4 off white", "Off-White Sail (Women's)", "La maison Off-White de Virgil Abloh a pris le monde d'assaut et cette Air Jordan 4 2020 est l'une de ses dernières adaptations de la silhouette rétro de Nike. La chaussure de basket classique présente une structure en cuir déconstruite, accentuée par du TPU semi-translucide sur les œillets, les ailes et le talon.", 1512, 0, "/images/articles/Air jordan 4 off white.png", "Air jordan 4 off white.png");